update_symlink_path(){
	if [[ -d "/usr/local/eosio/bin" ]]
	then
		if [[ ! -L "$TESTNET_BIN_DIR" ]]
		then
			printf "Please enter directory where Node was installed: "
			read answer
			GLOBAL_PATH=$answer
			cd $GLOBAL_PATH
			if [[ -L ${GLOBAL_PATH}/bin ]]
			then
				unlink $GLOBAL_PATH/bin
				ln -s /usr/local/eosio/bin $GLOBAL_PATH/bin
			fi
		elif [[ -L "$TESTNET_BIN_DIR" ]] 
			then
				unlink $TESTNET_BIN_DIR
				ln -s /usr/local/eosio/bin $GLOBAL_PATH/bin
		fi
	elif [[ -d "/usr/opt/eosio" ]]
	then
		if [[ ! -L "$TESTNET_BIN_DIR" ]]
		then
			printf "Please enter directory where Node was installed: "
			read answer
			GLOBAL_PATH=$answer
			cd $GLOBAL_PATH
			if [[ -L ${GLOBAL_PATH}/bin ]]
			then
				unlink $GLOBAL_PATH/bin
				if [[ $# == 0 ]]
				then
					ln -s /usr/opt/eosio/$EOS_VER/bin $GLOBAL_PATH/bin
				elif [[ $# > 0 ]]
				then
					ln -s /usr/opt/eosio/$RC_EOS_VER/bin $GLOBAL_PATH/bin
				fi
			fi
		elif [[ -L "$TESTNET_BIN_DIR" ]] 
			then
				unlink $TESTNET_BIN_DIR
				if [[ $# == 0 ]]
				then
					ln -s /usr/opt/eosio/$EOS_VER/bin $GLOBAL_PATH/bin
				elif [[ $# > 0 ]]
				then
					ln -s /usr/opt/eosio/$RC_EOS_VER/bin $GLOBAL_PATH/bin
				fi
		fi
	fi

}

update_from_source(){
	if [[ ! -d $EOS_SOURCE_DIR ]]
	then
		printf "Please enter directory where is EOSIO Sources: "
		read ans
		EOS_SOURCE_DIR="$ans/eos-source"
	fi
		cd $EOS_SOURCE_DIR
		git checkout -f
		git branch -f
		git pull
		if [[ $# == 0 ]]
		then
			git checkout $TAG
			git submodule update --init --recursive
			./eosio_build.sh -s EOS
			./eosio_install.sh
		elif [[ $# > 0 ]]
		then
			git checkout $1
			git submodule update --init --recursive
			./eosio_build.sh -s EOS
			./eosio_install.sh
		fi
}

update_eosio(){

	if [[ $# == 0 ]]
	then		
		if [[ $OS == "ubuntu" ]]
		then
			if [[ $OS_VER == "16.04" ]]
			then
				$GET_COMM $(curl -sS https://monitor.jungletestnet.io/version.json | jq '.ubuntu16_bin' | tr -d '"')
			    dpkg -i ./eosio*${EOS_VER}*.deb
				rm eosio*${EOS_VER}*.deb
			elif [[ $OS_VER == "18.04" ]]
			then
				$GET_COMM $(curl -sS https://monitor.jungletestnet.io/version.json | jq '.ubuntu18_bin' | tr -d '"')
			    dpkg -i ./eosio*${EOS_VER}*.deb
				rm eosio*${EOS_VER}*.deb
				#statements
			fi
		elif [[ $OS == "centos" ]] 
		then
			$GET_COMM $(curl -sS https://monitor.jungletestnet.io/version.json | jq '.centos7_bin' | tr -d '"')
			#rpm -Uvh ./eosio*${EOS_VER}*.rpm --nodeps
			yum -y update ./eosio*${EOS_VER}*.rpm
			rm ./eosio*${EOS_VER}*.rpm
		elif [[ $OS == "fedora" ]]
		then
			$GET_COMM $(curl -sS https://monitor.jungletestnet.io/version.json | jq '.fedora27_bin' | tr -d '"')
			#rpm -Uvh ./eosio*${EOS_VER}*.rpm --nodeps
			yum -y update ./eosio*${EOS_VER}*.rpm
			rm ./eosio*${EOS_VER}*.rpm
		fi
	elif [[ $# > 0 ]]
	then
		if [[ $OS == "ubuntu" ]]
		then
			if [[ $OS_VER == "16.04" ]]
			then
				$GET_COMM $(curl -sS https://monitor.jungletestnet.io/versionnew.json | jq '.rc.ubuntu16_bin' | tr -d '"')
			    dpkg -i ./eosio*${RC_EOS_VER}*.deb
				rm eosio*${RC_EOS_VER}*.deb
			elif [[ $OS_VER == "18.04" ]]
			then
				$GET_COMM $(curl -sS https://monitor.jungletestnet.io/versionnew.json | jq '.rc.ubuntu18_bin' | tr -d '"')
			    dpkg -i ./eosio*${RC_EOS_VER}*.deb
				rm eosio*${RC_EOS_VER}*.deb
			fi
		elif [[ $OS == "centos" ]] 
		then
			$GET_COMM $(curl -sS https://monitor.jungletestnet.io/versionnew.json | jq '.rc.centos7_bin' | tr -d '"')
			#rpm -Uvh ./eosio*${RC_EOS_VER}*.rpm --nodeps
			yum -y install ./eosio*${RC_EOS_VER}*.rpm
			rm ./eosio*${RC_EOS_VER}*.rpm
		elif [[ $OS == "fedora" ]]
		then
			$GET_COMM $(curl -sS https://monitor.jungletestnet.io/versionnew.json | jq '.rc.fedora27_bin' | tr -d '"')
			dnf install -y ./eosio*${RC_EOS_VER}*.rpm
			rm ./eosio*${RC_EOS_VER}*.rpm
		fi
		
	fi

	if [ $? -gt 0 ]
	then
		printf "\033[0;31mUpdate unsuccessfully\n\033[m"
		exit -1
	else
		if [[ $# == 0 ]]
		then
			update_symlink_path
		elif [[ $# > 0 ]]
		then
			update_symlink_path 1
		fi
		printf "\033[0;32mUpdated successfully\n\033[m"
	fi

}

